script:
frappe.ui.form.on('Budget', {
	refresh(frm) {
		// Aseguramos que s√≥lo se muestren los headings cuyo campo "budget" coincide con este presupuesto
		frm.fields_dict['custom_heading'].grid.get_field('heading').get_query = function(doc, cdt, cdn) {
			return {
				filters: [
					// Reemplaza "Custom Heading" por el nombre real del DocType al que apunta tu campo "heading"
					[ "Budget Heading", "budget", "=", frm.doc.name ]
				]
			};
		};
	}
});
